import star from "../images/star.png"

export default function Card(props) {
    let badgeText
    if (props.instance.openSpots === 0) {
        badgeText = "SOLD OUT"
    }
    else if (props.instance.location === "Online") {
        badgeText = "ONLINE"
    }
    return (
        <div className="card">
            {badgeText && <div className="card--badge">{badgeText}</div>}
            <img className="card--image" alt="card-image" src={require(`../../public/images/${props.instance.coverImg}`)}></img>
            <div className="card--stats">
                <img className="card--star" alt="card--rating" src={ star }></img>
                <span className="card--rating">&nbsp;{props.instance.stats.rating}&nbsp;</span>
                <span className="card--greyed"> ({props.instance.stats.reviewCount}) â€¢&nbsp;</span>
                <span className="card--greyed">{props.instance.location}</span>
            </div>
            <p className="card--title">{props.instance.title}</p>
            <p className="card--price"><b>From ${props.instance.price}</b> / person</p>
        </div>
    )
}
